import React, { Component } from 'react';
//import GetHomeService from '../../services/GetHomeService.js';
import axios from 'axios';

class Homes extends Component {

    constructor(props) {

        super(props);
    
        this.state = {
            // todos:
            //     [
            //         { id: 1, description: 'Learn to Dance', done: false, targetDate: new Date() },
            //         { id: 2, description: 'Become an Expert at React', done: false, targetDate: new Date() },
            //         { id: 3, description: 'Visit India', done: false, targetDate: new Date() }
            //     ],
            homelist: [

            ]
        }
     console.log("asda")

    }

    componentDidMount() {
       this.refresh();
    }
   
    refresh=()=>{
        axios.get('http://localhost:8080/homes').then(
            response => {
                if (response.status === 200) {
                    let data = response.data;
                    console.log("Logging state homelist")
                    console.log(data)

                }
                this.setState({ homelist: response.data })
            }
        );
    }

    render() {
        console.log(this.state.homelist)
        // this.setState({ homelist: this.hanldeSuccessCall })
        return (
            <>
                {/* <ShowHomes status={this.state.status} homelist={this.state.homelist}></ShowHomes> */}
                <div className="container bg-light">
                    <table className='table table-striped'>
                        <thead className='thead-dark'>
                            <tr>
                                <th>Description</th>
                                <th>Completed</th>
                                <th>Due Date</th></tr>
                        </thead>
                        <tbody>
                            {
                                this.state.homelist.map(
                                    home =>
                                        <tr key={home.homeId}>
                                            <td>{home.name}</td>
                                            <td>{home.url.toString()}</td>
                                            <td>{home.phone.toString()}</td>
                                            <td><button className='btn btn-warning' onClick={() =>this.deletehome(home.homeId)}>
                                                Delete</button>
                                                </td>
                                                <td><button className='btn btn-primary' onClick={() =>this.update(home.homeId)}>
                                                Update</button>
                                                </td>    
                                        </tr>
                                )
                            }

                        </tbody>

                    </table>
                </div>
            </>);


    }

    deletehome = (id) => {
        axios.delete(`http://localhost:8080/home/delete/${id}`).then(
            response => {
                if (response.status === 200) {
                    let data = response.data;
                    this.refresh();

                }
              
            }
        );

    }

    update = (id) => {
        
        this.props.history.push(`/newHome/${id}`)

    }




}




export default Homes;
