import React, { Component } from 'react';
import PropTypes from 'prop-types';

class Login extends Component {

    constructor(props) {

        super(props);
        this.state =

        {
            username: '',
            password: '',
            status: -1
        }

    }

    handleChange = (event) => {

        this.setState({ [event.target.name]: event.target.value });
    }

    checkLogin = () => {

        if (this.state.username === 'teo' && this.state.password === '123')
            this.setState({ status: 1 })
        else
            this.setState({ status: 0 })
    }

    render() {
        return (
            <div className="Login">
                <ShowLogin status={this.state.status}></ShowLogin>
                Username :<input type="text" name="username" onChange={this.handleChange} value={this.state.username} />
                <br></br>
                <br></br>
                Password :<input type="password" name="password" onChange={this.handleChange} value={this.state.password} />
                <br></br>
                <br></br>
                <button onClick={this.checkLogin}></button>
            </div>
        );


    }


}




function ShowLogin(props) {

    if (this.props.status === 0)
        return (
            <div>
                Login Failed!
            </div>
        )
    else if (this.props.status === 1)
        return (
            <div>
                Login Succeeded!
            </div>
        )
    else
        return null;


}

ShowLogin.defaultProps = {
    status: -1,

}

ShowLogin.propTypes = {
    status: PropTypes.number
}



export default Login;
